import React, { useState, useEffect } from "react";
import "./create_blog.css";

function Create() {



    return (
        <div className="forms">
            <h1>Create a Blog!</h1>
            <form action="/submit" method="POST">
            <label for="name"><b>Name:</b></label>
            <br></br>
            <input type="text" name="name" id="name" className="name" required placeholder="Enter your name"></input>
            <br></br>
            <label for="blog"><b>Blog:</b></label>
            <br></br>
            <textarea name="blog" id="blog" class="blog" required placeholder="Share your thoughts!" rows="5" cols="50"></textarea>
            <br></br>
            <center>
            <input type="submit" value="Submit" className="submit"></input>
            </center>
            </form>
        </div>
    );
}

export default Create;
