import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import "./create_blog.css";
import { motion } from 'framer-motion';

function Create() {
    // Define state for form inputs
    const [name, setName] = useState('');
    const [blog, setBlog] = useState('');
    const [isSignedIn, setIsSignedIn] = useState(false);
    const [showModal, setShowModal] = useState(true);

    const handleSignIn = () => {
        setIsSignedIn(true);
        setShowModal(false); // Close the modal after sign-in
      };
    // Handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault(); // Prevent page reload on form submission

        // Make a POST request to the backend with form data
        const response = await fetch(`/api/submit`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ name, blog }),
        });

        if (response.ok) {
            const data = await response.json();
            alert(data.message); // Show success message from Flask
            setName(''); 
            setBlog('');
        } else {
            alert("Error submitting blog post"); 
        }
    };

    return (
        <div>
      {/* Sign-In Modal */}
      {showModal && (
        <motion.div
          initial={{ y: '100vh', x:'50%' }}
          animate={{ y: 0 }}
          exit={{ y: '100vh' }}
          transition={{ type: 'spring', damping: 25, stiffness: 120 }}
          style={{
            position: 'fixed',
            bottom: 0,
            background: 'white',
            boxShadow: '0px -2px 10px rgba(0,0,0,0.2)',
            zIndex: 10,
          }}
        >
          <div style={{ padding: '20px', textAlign: 'center' }}>
            <h2>Sign In</h2>
            <p>Please sign in to access the content</p>
            <button onClick={handleSignIn}>Sign In</button>
          </div>
        </motion.div>
      )}


       {isSignedIn && (
        <div>
            <nav>
                <ul>
                    <li><Link to="/">Create</Link></li>
                    <li><Link to="/Blog">Blog</Link></li>
                </ul>
            </nav>
            <center>
                <div className="forms">
                    <h1>Create a Blog!</h1>
                    <form onSubmit={handleSubmit}> {/* Update form's submit handler */}
                        <center>
                            <label htmlFor="name"><b>NAME</b></label>
                            <br />
                            <input
                                type="text"
                                name="name"
                                id="name"
                                className="name"
                                required
                                placeholder="Enter your name"
                                value={name}
                                onChange={(e) => setName(e.target.value)}
                            />
                            <br />
                            <label htmlFor="blog"><b>BLOG</b></label>
                            <br />
                            <textarea
                                name="blog"
                                id="blog"
                                className="blog"
                                required
                                placeholder="Share your thoughts!"
                                rows="5"
                                cols="50"
                                value={blog}
                                onChange={(e) => setBlog(e.target.value)}
                            />
                            <br />
                            <input type="submit" value="Submit" className="submit" />
                        </center>
                    </form>
                </div>
            </center>
        </div>
         )}
    </div>
    );
}

export default Create;
